implementation:
cc.state.set(
  ${inputs.key},
  ${inputs.value}${
    inputs.persist || inputs.ttl > 0
      ? `, {${[
          inputs.persist ? ` persist: "${inputs.persist}"` : '',
          inputs.ttl > 0 ? ` ttl: ${inputs.ttl}` : ''
        ].filter(Boolean).join(', ')} }`
      : ''
  }
);
